  const [playingId, setPlayingId] = useState(null);
  const audioRef = useRef(null);

  useEffect(() => { fetchSamples(); }, []);

  const fetchSamples = async () => {
    try {
      // FIXED: Added /api prefix
      const res = await fetch(`${API_BASE_URL}/api/samples`);
      const data = await res.json();
      setSamples(data.samples || []);
    } catch (e) {
      console.error(e);
    } finally {
      setLoading(false);
    }
  };

  const downloadAudio = (filename) => {
    const link = document.createElement('a');
    // FIXED: Use API_BASE_URL instead of hardcoded
    link.href = `${API_BASE_URL}/api/samples/download/${filename}`;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  // ... rest of SampleLibrary component

  return (
    <div className="space-y-6">
      {/* ... your existing JSX ... */}
    </div>
  );
}

