import React, { useState, useRef } from 'react';
import { Upload, Play, Pause, Download, CheckCircle, AlertCircle, Loader, FileAudio, Moon, Sun } from 'lucide-react';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://127.0.0.1:5000/api';

export default function SpeakUpApp() {
  const [currentPage, setCurrentPage] = useState('analyze');
  const [step, setStep] = useState('input');
  const [topic, setTopic] = useState('');
  const [audioFile, setAudioFile] = useState(null);
  const [audioURL, setAudioURL] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [results, setResults] = useState(null);
  const [error, setError] = useState(null);
  const [darkMode, setDarkMode] = useState(false);

  const audioRef = useRef(null);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setAudioFile(file);
      setAudioURL(URL.createObjectURL(file));
      setStep('preview');
    }
  };

  const togglePlayback = () => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const analyzeAudio = async () => {
    if (!topic.trim()) {
      setError('Please enter a topic');
      return;
    }
    if (!audioFile) {
      setError('Please upload audio');
      return;
    }

    setStep('uploading');
    setError(null);

    const formData = new FormData();
    formData.append('audio', audioFile);
    formData.append('topic', topic);

    try {
      const response = await fetch(`${API_BASE_URL}/analyze`, {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (data.success) {
        setResults(data);
        setStep('results');
      } else {
        setError(data.error || 'Analysis failed');
        setStep('preview');
      }
    } catch (err) {
      setError('Connection failed. Make sure the backend is running.');
      setStep('preview');
    }
  };

  const downloadDocument = () => {
    if (results && results.document_url) {
      window.open(`http://127.0.0.1:5000${results.document_url}`, '_blank');
    }
  };

  const reset = () => {
    setStep('input');
    setTopic('');
    setAudioFile(null);
    setAudioURL(null);
    setResults(null);
    setError(null);
    setIsPlaying(false);
  };

  const getScoreColor = (score, max) => {
    const percentage = (score / max) * 100;
    if (percentage >= 80) return 'text-green-600';
    if (percentage >= 60) return 'text-yellow-600';
    return 'text-red-600';
  };

  return (
    <div className={`min-h-screen transition-colors duration-300 ${
      darkMode 
        ? 'bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900' 
        : 'bg-gradient-to-br from-indigo-50 via-white to-purple-50'
    }`}>
      <header className={`shadow-sm sticky top-0 z-50 transition-colors duration-300 ${
        darkMode ? 'bg-gray-800 border-b border-gray-700' : 'bg-white'
      }`}>
        <div className="max-w-7xl mx-auto px-6 py-4 flex items-center justify-between">
          <div className="flex items-center gap-4">
            <h1 className={`text-3xl font-bold bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent`}>
              SpeakUp AI
            </h1>
          </div>

          <div className="flex items-center gap-6">
            <nav className="flex gap-4">
              <button
                onClick={() => { setCurrentPage('analyze'); reset(); }}
                className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                  currentPage === 'analyze' 
                    ? 'bg-indigo-600 text-white' 
                    : darkMode 
                      ? 'text-gray-300 hover:text-indigo-400' 
                      : 'text-gray-600 hover:text-indigo-600'
                }`}
              >
                Analyze Speech
              </button>
              <button
                onClick={() => setCurrentPage('samples')}
                className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                  currentPage === 'samples' 
                    ? 'bg-indigo-600 text-white' 
                    : darkMode 
                      ? 'text-gray-300 hover:text-indigo-400' 
                      : 'text-gray-600 hover:text-indigo-600'
                }`}
              >
                Sample Library
              </button>
            </nav>

            <button
              onClick={() => setDarkMode(!darkMode)}
              className={`p-2 rounded-lg transition-colors ${
                darkMode 
                  ? 'bg-gray-700 text-yellow-400 hover:bg-gray-600' 
                  : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
              }`}
              aria-label="Toggle dark mode"
            >
              {darkMode ? <Sun size={20} /> : <Moon size={20} />}
            </button>

            <img 
              src="/logo.png" 
              alt="School Logo" 
              className={`h-12 w-12 rounded-full object-cover border-2 ${
                darkMode ? 'border-indigo-500' : 'border-indigo-200'
              }`}
              onError={(e) => {
                e.target.src = 'data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><circle cx="50" cy="50" r="40" fill="%234F46E5"/><text x="50" y="60" text-anchor="middle" fill="white" font-size="35" font-family="Arial" font-weight="bold">HS</text></svg>';
              }}
            />
          </div>
        </div>
      </header>

      <div className="max-w-4xl mx-auto p-6">
        {currentPage === 'analyze' ? (
          <>
            <div className="text-center mb-8 pt-4">
              <p className="text-gray-600">Master your speaking skills with AI-powered feedback</p>
            </div>

            {error && (
              <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-lg flex items-center gap-3">
                <AlertCircle className="text-red-600" size={20} />
                <span className="text-red-700">{error}</span>
              </div>
            )}

            <div className="bg-white rounded-2xl shadow-xl p-8">
              {step === 'input' && (
                <div className="space-y-6">
                  <div>
                    <label className={`block text-sm font-medium mb-2 ${
                      darkMode ? 'text-gray-300' : 'text-gray-700'
                    }`}>
                      Topic / Question
                    </label>
                    <textarea
                      value={topic}
                      onChange={(e) => setTopic(e.target.value)}
                      placeholder="Enter the topic or question you'll be speaking about..."
                      className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent resize-none transition-colors ${
                        darkMode 
                          ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                          : 'bg-white border-gray-300 text-gray-900'
                      }`}
                      rows="4"
                    />
                  </div>

                  <div>
                    <label className="flex items-center justify-center gap-2 px-6 py-4 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition-colors cursor-pointer">
                      <Upload size={20} />
                      Upload Audio File
                      <input
                        type="file"
                        accept="audio/*"
                        onChange={handleFileUpload}
                        className="hidden"
                      />
                    </label>
                  </div>

                  <p className={`text-sm text-center ${
                    darkMode ? 'text-gray-400' : 'text-gray-500'
                  }`}>
                    Accepted formats: MP3, WAV, M4A, WEBM, OGG  Maximum duration: 5 minutes
                  </p>
                </div>
              )}

              {step === 'preview' && (
                <div className="space-y-6">
                  <div className="text-center">
                    <div className={`inline-flex items-center justify-center w-24 h-24 rounded-full mb-4 ${
                      darkMode ? 'bg-indigo-900 bg-opacity-30' : 'bg-indigo-100'
                    }`}>
                      <FileAudio className="text-indigo-600" size={40} />
                    </div>
                    <h3 className={`text-xl font-semibold mb-2 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
                      Audio Ready
                    </h3>
                    <p className={darkMode ? 'text-gray-400' : 'text-gray-600'}>
                      {audioFile?.name}
                    </p>
                  </div>

                  <div className="flex justify-center gap-4">
                    <button
                      onClick={togglePlayback}
                      className="flex items-center gap-2 px-6 py-3 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors"
                    >
                      {isPlaying ? <Pause size={20} /> : <Play size={20} />}
                      {isPlaying ? 'Pause' : 'Play'}
                    </button>
                    <button
                      onClick={analyzeAudio}
                      className="flex items-center gap-2 px-6 py-3 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition-colors"
                    >
                      <CheckCircle size={20} />
                      Analyze
                    </button>
                  </div>
                  <audio ref={audioRef} src={audioURL} onEnded={() => setIsPlaying(false)} />

                  <div className="flex justify-center">
                    <button
                      onClick={reset}
                      className={`text-sm transition-colors ${
                        darkMode 
                          ? 'text-gray-400 hover:text-gray-200' 
                          : 'text-gray-600 hover:text-gray-800'
                      }`}
                    >
                      Start Over
                    </button>
                  </div>
                </div>
              )}

              {step === 'uploading' && (
                <div className="text-center py-12">
                  <Loader className={`animate-spin mx-auto mb-4 ${
                    darkMode ? 'text-indigo-400' : 'text-indigo-600'
                  }`} size={48} />
                  <h3 className={`text-xl font-semibold mb-2 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
                    Analyzing your speech...
                  </h3>
                  <p className={darkMode ? 'text-gray-400' : 'text-gray-600'}>
                    This may take a minute
                  </p>
                </div>
              )}

              {step === 'results' && results && (
                <div className="space-y-6">
                  <div className="text-center pb-6 border-b">
                    <h3 className="text-2xl font-bold mb-2">Your Results</h3>
                    <div className="text-5xl font-bold text-indigo-600">
                      {results.scores.total.toFixed(2)}/2.0
                    </div>
                  </div>

                  <div className="grid grid-cols-3 gap-4">
                    <div className="text-center p-4 bg-gray-50 rounded-lg">
                      <div className="text-sm text-gray-600 mb-1">Content</div>
                      <div className={`text-2xl font-bold ${getScoreColor(results.scores.content, 0.9)}`}>
                        {results.scores.content.toFixed(2)}/0.9
                      </div>
                    </div>
                    <div className="text-center p-4 bg-gray-50 rounded-lg">
                      <div className="text-sm text-gray-600 mb-1">Accuracy</div>
                      <div className={`text-2xl font-bold ${getScoreColor(results.scores.accuracy, 0.6)}`}>
                        {results.scores.accuracy.toFixed(2)}/0.6
                      </div>
                    </div>
                    <div className="text-center p-4 bg-gray-50 rounded-lg">
                      <div className="text-sm text-gray-600 mb-1">Delivery</div>
                      <div className={`text-2xl font-bold ${getScoreColor(results.scores.delivery, 0.5)}`}>
                        {results.scores.delivery.toFixed(2)}/0.5
                      </div>
                    </div>
                  </div>

                  <div className="space-y-4">
                    <div className={`p-4 rounded-lg ${
                      darkMode ? 'bg-blue-900 bg-opacity-30' : 'bg-blue-50'
                    }`}>
                      <h4 className={`font-semibold mb-2 ${
                        darkMode ? 'text-blue-300' : 'text-blue-900'
                      }`}> Content</h4>
                      <p className={`text-sm ${darkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                        {results.feedback.content}
                      </p>
                    </div>
                    <div className={`p-4 rounded-lg ${
                      darkMode ? 'bg-green-900 bg-opacity-30' : 'bg-green-50'
                    }`}>
                      <h4 className={`font-semibold mb-2 ${
                        darkMode ? 'text-green-300' : 'text-green-900'
                      }`}> Accuracy</h4>
                      <p className={`text-sm ${darkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                        {results.feedback.accuracy}
                      </p>
                    </div>
                    <div className={`p-4 rounded-lg ${
                      darkMode ? 'bg-purple-900 bg-opacity-30' : 'bg-purple-50'
                    }`}>
                      <h4 className={`font-semibold mb-2 ${
                        darkMode ? 'text-purple-300' : 'text-purple-900'
                      }`}> Delivery</h4>
                      <p className={`text-sm ${darkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                        {results.feedback.delivery}
                      </p>
                    </div>
                  </div>

                  {results.sample_response && (
                    <div className={`mt-6 p-5 rounded-lg border ${
                      darkMode 
                        ? 'bg-yellow-900 bg-opacity-20 border-yellow-700' 
                        : 'bg-gradient-to-br from-yellow-50 to-orange-50 border-yellow-200'
                    }`}>
                      <h4 className={`font-semibold mb-3 flex items-center gap-2 ${
                        darkMode ? 'text-yellow-300' : 'text-yellow-900'
                      }`}>
                         Sample 2.0/2.0 Response
                      </h4>
                      <div className={`text-sm leading-relaxed whitespace-pre-line ${
                        darkMode ? 'text-gray-300' : 'text-gray-800'
                      }`}>
                        {results.sample_response}
                      </div>
                    </div>
                  )}

                  <div className="flex gap-4 pt-4">
                    <button
                      onClick={downloadDocument}
                      className="flex-1 flex items-center justify-center gap-2 px-6 py-3 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition-colors"
                    >
                      <Download size={20} />
                      Download Full Report
                    </button>
                    <button
                      onClick={reset}
                      className={`px-6 py-3 rounded-lg transition-colors ${
                        darkMode 
                          ? 'bg-gray-700 text-gray-300 hover:bg-gray-600' 
                          : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                      }`}
                    >
                      Try Again
                    </button>
                  </div>
                </div>
              )}
            </div>

            <div className="text-center mt-8 text-sm text-gray-500">
              <p>Powered by Groq AI  Made with  for language learners</p>
            </div>
          </>
        ) : (
          <SampleLibrary />
        )}
      </div>
    </div>
  );
}

function SampleLibrary({ darkMode }) {
  const [samples, setSamples] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSample, setSelectedSample] = useState(null);

  React.useEffect(() => {
    fetchSamples();
  }, []);

  const fetchSamples = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/samples`);
      const data = await response.json();
      setSamples(data.samples || []);
    } catch (err) {
      console.error('Failed to fetch samples');
    } finally {
      setLoading(false);
    }
  };

  const filteredSamples = samples.filter(sample => 
    sample.topic.toLowerCase().includes(searchTerm.toLowerCase()) ||
    sample.tags?.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  if (loading) {
    return (
      <div className="text-center py-12">
        <Loader className={`animate-spin mx-auto mb-4 ${
          darkMode ? 'text-indigo-400' : 'text-indigo-600'
        }`} size={48} />
        <p className={darkMode ? 'text-gray-300' : 'text-gray-600'}>Loading samples...</p>
      </div>
    );
  }

  return (
    <div className="space-y-6 py-8">
      <div className="text-center mb-8">
        <h2 className={`text-3xl font-bold mb-2 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
          Sample Speech Library
        </h2>
        <p className={darkMode ? 'text-gray-300' : 'text-gray-600'}>
          Listen to high-scoring sample speeches
        </p>
      </div>

      <div className={`rounded-lg shadow-md p-4 ${
        darkMode ? 'bg-gray-800 border border-gray-700' : 'bg-white'
      }`}>
        <input
          type="text"
          placeholder="Search by topic or tag..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-colors ${
            darkMode 
              ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
              : 'bg-white border-gray-300'
          }`}
        />
      </div>

      {filteredSamples.length === 0 ? (
        <div className={`text-center py-12 rounded-lg shadow-md ${
          darkMode ? 'bg-gray-800 border border-gray-700' : 'bg-white'
        }`}>
          <FileAudio className={`mx-auto mb-4 ${
            darkMode ? 'text-gray-600' : 'text-gray-400'
          }`} size={48} />
          <p className={darkMode ? 'text-gray-300' : 'text-gray-600'}>No samples found</p>
          <p className={`text-sm mt-2 ${darkMode ? 'text-gray-400' : 'text-gray-500'}`}>
            Try a different search term
          </p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {filteredSamples.map((sample) => (
            <div
              key={sample.id}
              className={`rounded-lg shadow-md p-6 hover:shadow-lg transition-shadow cursor-pointer ${
                darkMode ? 'bg-gray-800 border border-gray-700' : 'bg-white'
              }`}
              onClick={() => setSelectedSample(sample)}
            >
              <div className="flex items-start justify-between mb-3">
                <div className="flex-1">
                  <h3 className={`font-semibold text-lg mb-1 ${
                    darkMode ? 'text-white' : 'text-gray-900'
                  }`}>{sample.topic}</h3>
                  <p className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                    Duration: {Math.floor(sample.duration / 60)}:{(sample.duration % 60).toString().padStart(2, '0')}
                  </p>
                </div>
                <div className={`text-2xl font-bold ${
                  darkMode ? 'text-indigo-400' : 'text-indigo-600'
                }`}>
                  {sample.score}/2.0
                </div>
              </div>

              {sample.tags && (
                <div className="flex flex-wrap gap-2 mt-3">
                  {sample.tags.map((tag, idx) => (
                    <span 
                      key={idx} 
                      className={`px-2 py-1 text-xs rounded-full ${
                        darkMode 
                          ? 'bg-indigo-900 bg-opacity-50 text-indigo-300' 
                          : 'bg-indigo-100 text-indigo-700'
                      }`}
                    >
                      {tag}
                    </span>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      )}

      {selectedSample && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className={`rounded-2xl max-w-2xl w-full max-h-[90vh] overflow-y-auto p-8 ${
            darkMode ? 'bg-gray-800' : 'bg-white'
          }`}>
            <div className="flex justify-between items-start mb-6">
              <div>
                <h3 className={`text-2xl font-bold mb-2 ${
                  darkMode ? 'text-white' : 'text-gray-900'
                }`}>{selectedSample.topic}</h3>
                <div className={`text-3xl font-bold ${
                  darkMode ? 'text-indigo-400' : 'text-indigo-600'
                }`}>{selectedSample.score}/2.0</div>
              </div>
              <button
                onClick={() => setSelectedSample(null)}
                className={`text-2xl transition-colors ${
                  darkMode 
                    ? 'text-gray-400 hover:text-gray-200' 
                    : 'text-gray-400 hover:text-gray-600'
                }`}
              >

              </button>
            </div>

            {selectedSample.transcript && (
              <div className="mb-6">
                <h4 className={`font-semibold mb-2 ${
                  darkMode ? 'text-gray-200' : 'text-gray-900'
                }`}>Transcript</h4>
                <p className={`text-sm leading-relaxed rounded-lg p-4 ${
                  darkMode 
                    ? 'text-gray-300 bg-gray-700' 
                    : 'text-gray-700 bg-gray-50'
                }`}>
                  {selectedSample.transcript}
                </p>
              </div>
            )}

            {selectedSample.feedback && (
              <div className="space-y-3">
                <h4 className={`font-semibold ${
                  darkMode ? 'text-gray-200' : 'text-gray-900'
                }`}>Why This Speech Scored High</h4>
                <p className={`text-sm rounded-lg p-4 ${
                  darkMode 
                    ? 'text-gray-300 bg-green-900 bg-opacity-30' 
                    : 'text-gray-700 bg-green-50'
                }`}>
                  {selectedSample.feedback}
                </p>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
