function AdminPanel({ onClose }) {
  const [activeTab, setActiveTab] = useState('upload');
  const [audioFile, setAudioFile] = useState(null);
  const [topic, setTopic] = useState('');
  const [question, setQuestion] = useState('');
  const [speaker, setSpeaker] = useState('');
  const [score, setScore] = useState('2.0');
  const [transcript, setTranscript] = useState('');
  const [feedback, setFeedback] = useState('');
  const [uploading, setUploading] = useState(false);
  const [message, setMessage] = useState('');
  const [samples, setSamples] = useState([]);
  const [loadingSamples, setLoadingSamples] = useState(true);
  const [editingId, setEditingId] = useState(null);
  const [editData, setEditData] = useState({});

  const [questions, setQuestions] = useState([]);
  const [loadingQuestions, setLoadingQuestions] = useState(true);
  const [newQuestionTopic, setNewQuestionTopic] = useState('');
  const [newQuestionText, setNewQuestionText] = useState('');
  const [newQuestionCategory, setNewQuestionCategory] = useState('General');
  const [editingQuestionId, setEditingQuestionId] = useState(null);
  const [editQuestionData, setEditQuestionData] = useState({});

  useEffect(() => {
    if (activeTab === 'manage') fetchSamples();
    if (activeTab === 'questions') fetchQuestions();
  }, [activeTab]);

  const fetchSamples = async () => {
    setLoadingSamples(true);
    try {
      const res = await fetch(`${API_BASE_URL}/samples`);
      const data = await res.json();
      setSamples(data.samples || []);
    } catch (e) {
      console.error(e);
    } finally {
      setLoadingSamples(false);
    }
  };

  const fetchQuestions = async () => {
    setLoadingQuestions(true);
    try {
      const res = await fetch(`${API_BASE_URL}/questions`);
      const data = await res.json();
      setQuestions(data.questions || []);
    } catch (e) {
      console.error(e);
    } finally {
      setLoadingQuestions(false);
    }
  };

  const handleUpload = async () => {
    if (!audioFile || !topic || !speaker || !transcript || !feedback) {
      setMessage('Please fill in all fields');
      return;
    }

    setUploading(true);
    setMessage('');

    const formData = new FormData();
    formData.append('audio', audioFile);
    formData.append('topic', topic);
    formData.append('question', question);
    formData.append('speaker', speaker);
    formData.append('score', score);
    formData.append('transcript', transcript);
    formData.append('feedback', feedback);

    try {
      const response = await fetch(`${API_BASE_URL}/samples/upload`, {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      if (data.success) {
        setMessage('âœ“ Sample uploaded successfully!');
        setAudioFile(null);
        setTopic('');
        setQuestion('');
        setSpeaker('');
        setScore('2.0');
        setTranscript('');
        setFeedback('');
      } else setMessage('Error: ' + (data.error || 'Upload failed'));
    } catch (err) {
      setMessage('Error: Connection failed');
    } finally {
      setUploading(false);
    }
  };

  const startEdit = (sample) => {
    setEditingId(sample.id);
    setEditData({
      topic: sample.topic || '',
      question: sample.question || '',
      speaker: sample.speaker || '',
      score: String(sample.score || ''),
      transcript: sample.transcript || '',
      feedback: sample.feedback || '',
    });
  };

  const cancelEdit = () => {
    setEditingId(null);
    setEditData({});
  };

  const saveEdit = async () => {
    try {
      const form = new FormData();
      form.append('topic', editData.topic);
      form.append('speaker', editData.speaker);
      form.append('score', editData.score);
      form.append('question', editData.question);
      form.append('transcript', editData.transcript);
      form.append('feedback', editData.feedback);

      const res = await fetch(`${API_BASE_URL}/samples/${editingId}`, {
        method: 'PUT',
        body: form,
      });
      const data = await res.json();
      if (data.success) {
        setMessage('âœ“ Updated successfully');
        await fetchSamples();
        cancelEdit();
      } else setMessage('Error: ' + (data.error || 'Update failed'));
    } catch (e) {
      setMessage('Error: Connection failed');
    }
  };

  const deleteSample = async (id) => {
