      const data = await res.json();
      if (data.success) {
        setMessage('âœ“ Deleted successfully');
        await fetchSamples();
      } else setMessage('Error: ' + (data.error || 'Delete failed'));
    } catch (e) {
      setMessage('Error: Connection failed');
    }
  };

