import React, { useState, useRef, useEffect } from 'react';
import { Upload, Play, Pause, Download, CheckCircle, AlertCircle, Loader, FileAudio, Moon, Sun, Settings, Lock } from 'lucide-react';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://127.0.0.1:5000/api';
const ADMIN_PASSWORD = 'speakup2024'; // Change this to your desired password

export default function SpeakUpApp() {
  const [currentPage, setCurrentPage] = useState('analyze');
  const [step, setStep] = useState('input');
  const [topic, setTopic] = useState('');
  const [audioFile, setAudioFile] = useState(null);
  const [audioURL, setAudioURL] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [results, setResults] = useState(null);
  const [error, setError] = useState(null);
  const [darkMode, setDarkMode] = useState(false);
  const [showAdminPanel, setShowAdminPanel] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const audioRef = useRef(null);

  useEffect(() => {
    const saved = localStorage.getItem('darkMode');
    if (saved) setDarkMode(JSON.parse(saved));
  }, []);

  useEffect(() => {
    localStorage.setItem('darkMode', JSON.stringify(darkMode));
  }, [darkMode]);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setAudioFile(file);
      setAudioURL(URL.createObjectURL(file));
      setStep('preview');
    }
  };

  const togglePlayback = () => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const analyzeAudio = async () => {
    if (!topic.trim()) {
      setError('Please enter a topic');
      return;
    }
    if (!audioFile) {
      setError('Please upload audio');
      return;
    }

    setStep('uploading');
    setError(null);

    const formData = new FormData();
    formData.append('audio', audioFile);
    formData.append('topic', topic);

    try {
      const response = await fetch(`${API_BASE_URL}/analyze`, {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (data.success) {
        setResults(data);
        setStep('results');
      } else {
        setError(data.error || 'Analysis failed');
        setStep('preview');
      }
    } catch (err) {
      setError('Connection failed. Make sure the backend is running.');
      setStep('preview');
    }
  };

  const downloadDocument = () => {
    if (results && results.document_url) {
      window.open(`http://127.0.0.1:5000${results.document_url}`, '_blank');
    }
  };

  const reset = () => {
    setStep('input');
    setTopic('');
    setAudioFile(null);
    setAudioURL(null);
    setResults(null);
    setError(null);
    setIsPlaying(false);
  };

  const getScoreColor = (score, max) => {
    const percentage = (score / max) * 100;
    if (percentage >= 80) return darkMode ? 'text-green-400' : 'text-green-600';
    if (percentage >= 60) return darkMode ? 'text-yellow-400' : 'text-yellow-600';
    return darkMode ? 'text-red-400' : 'text-red-600';
  };

  const openAdminPanel = () => {
    const password = prompt('Enter admin password:');
    if (password === ADMIN_PASSWORD) {
      setIsAuthenticated(true);
      setShowAdminPanel(true);
    } else if (password !== null) {
      alert('Incorrect password!');
    }
  };

  return (
    <div className={`min-h-screen ${
      darkMode 
        ? 'bg-gradient-to-br from-gray-900 via-slate-900 to-gray-900' 
        : 'bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50'
    }`}>
      <header className={`backdrop-blur-lg sticky top-0 z-50 border-b ${
        darkMode 
          ? 'bg-gray-900/70 border-gray-800' 
          : 'bg-white/70 border-gray-200'
      }`}>
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <div className={`w-10 h-10 rounded-xl flex items-center justify-center font-bold text-lg ${
                darkMode 
                  ? 'bg-gradient-to-br from-indigo-600 to-purple-600' 
                  : 'bg-gradient-to-br from-indigo-500 to-purple-500'
              } text-white shadow-lg`}>
                S
              </div>
              <h1 className={`text-2xl font-bold ${
                darkMode ? 'text-white' : 'text-gray-900'
              }`}>
                SpeakUp AI
              </h1>
            </div>

            <div className="flex items-center gap-4">
              <nav className="flex gap-2">
                <button
                  onClick={() => { setCurrentPage('analyze'); reset(); }}
                  className={`px-5 py-2.5 rounded-xl font-medium ${
                    currentPage === 'analyze' 
                      ? darkMode
                        ? 'bg-indigo-600 text-white shadow-lg shadow-indigo-500/50'
                        : 'bg-gradient-to-r from-indigo-600 to-purple-600 text-white shadow-lg shadow-indigo-300/50'
                      : darkMode 
                        ? 'text-gray-300 hover:bg-gray-800' 
                        : 'text-gray-600 hover:bg-gray-100'
                  }`}
                >
                  Analyze
                </button>
                <button
                  onClick={() => setCurrentPage('samples')}
                  className={`px-5 py-2.5 rounded-xl font-medium ${
                    currentPage === 'samples' 
                      ? darkMode
                        ? 'bg-indigo-600 text-white shadow-lg shadow-indigo-500/50'
                        : 'bg-gradient-to-r from-indigo-600 to-purple-600 text-white shadow-lg shadow-indigo-300/50'
                      : darkMode 
                        ? 'text-gray-300 hover:bg-gray-800' 
                        : 'text-gray-600 hover:bg-gray-100'
                  }`}
                >
                  Samples
                </button>
                <button
                  onClick={openAdminPanel}
                  className={`p-2.5 rounded-xl ${
                    darkMode 
                      ? 'text-gray-400 hover:bg-gray-800 hover:text-gray-200' 
                      : 'text-gray-400 hover:bg-gray-100 hover:text-gray-600'
                  }`}
                  title="Admin Panel"
                >
                  <Settings size={20} />
                </button>
              </nav>

              <button
                onClick={() => setDarkMode(!darkMode)}
                className={`p-2.5 rounded-xl ${
                  darkMode 
                    ? 'bg-gray-800 text-yellow-400 hover:bg-gray-700' 
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                {darkMode ? <Sun size={20} /> : <Moon size={20} />}
              </button>

              <img 
                src="/logo.png" 
                alt="School Logo" 
                className={`h-11 w-11 rounded-xl object-cover border-2 ${
                  darkMode ? 'border-indigo-500/50' : 'border-indigo-300/50'
                }`}
                onError={(e) => {
                  e.target.src = 'data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><circle cx="50" cy="50" r="40" fill="%234F46E5"/><text x="50" y="60" text-anchor="middle" fill="white" font-size="35" font-family="Arial" font-weight="bold">HS</text></svg>';
                }}
              />
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-5xl mx-auto px-6 py-8">
        {currentPage === 'analyze' ? (
          <>
            <div className="text-center mb-10">
              <h2 className={`text-4xl font-bold mb-3 ${
                darkMode ? 'text-white' : 'text-gray-900'
              }`}>
                {step === 'results' ? 'Your Speech Analysis' : 'Analyze Your Speech'}
              </h2>
              <p className={`text-lg ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                Master NEC Speaking with AI-powered feedback
              </p>
            </div>

            {error && (
              <div className={`mb-6 p-4 rounded-2xl flex items-center gap-3 border backdrop-blur-sm ${
                darkMode 
                  ? 'bg-red-500/10 border-red-500/30 text-red-300' 
                  : 'bg-red-50 border-red-200 text-red-700'
              }`}>
                <AlertCircle size={20} />
                <span className="font-medium">{error}</span>
              </div>
            )}

            <div className={`rounded-3xl shadow-2xl p-8 backdrop-blur-sm border ${
              darkMode 
                ? 'bg-gray-800/50 border-gray-700/50' 
                : 'bg-white/80 border-white/50'
            }`}>
              {step === 'input' && (
                <div className="space-y-6">
                  <div>
                    <label className={`block text-sm font-semibold mb-3 ${
                      darkMode ? 'text-gray-200' : 'text-gray-700'
                    }`}>
                      Speaking Topic
                    </label>
                    <textarea
                      value={topic}
                      onChange={(e) => setTopic(e.target.value)}
                      placeholder="Enter the topic or question you'll be speaking about..."
                      className={`w-full px-5 py-4 rounded-2xl focus:ring-2 focus:ring-indigo-500 focus:outline-none resize-none font-medium ${
                        darkMode 
                          ? 'bg-gray-900/50 border-2 border-gray-700 text-white placeholder-gray-500' 
                          : 'bg-gray-50 border-2 border-gray-200 text-gray-900 placeholder-gray-400'
                      }`}
                      rows="5"
                    />
                  </div>

                  <div>
                    <label className={`flex items-center justify-center gap-3 px-8 py-5 rounded-2xl font-semibold cursor-pointer shadow-lg ${
                      darkMode
                        ? 'bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-500 hover:to-purple-500 text-white'
                        : 'bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 text-white'
                    }`}>
                      <Upload size={22} />
                      <span>Upload Audio File</span>
                      <input
                        type="file"
                        accept="audio/*"
                        onChange={handleFileUpload}
                        className="hidden"
                      />
                    </label>
                  </div>

                  <p className={`text-sm text-center ${
                    darkMode ? 'text-gray-400' : 'text-gray-500'
                  }`}>
                    Supported: MP3, WAV, M4A, WEBM, OGG â€¢ Max 5 minutes
                  </p>
                </div>
              )}

              {step === 'preview' && (
                <div className="space-y-8">
                  <div className="text-center">
                    <div className={`inline-flex items-center justify-center w-24 h-24 rounded-3xl mb-5 shadow-lg ${
                      darkMode 
                        ? 'bg-gradient-to-br from-indigo-600/30 to-purple-600/30' 
                        : 'bg-gradient-to-br from-indigo-100 to-purple-100'
                    }`}>
                      <FileAudio className={darkMode ? 'text-indigo-400' : 'text-indigo-600'} size={40} />
                    </div>
                    <h3 className={`text-2xl font-bold mb-2 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
                      Ready to Analyze
                    </h3>
                    <p className={`font-medium ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                      {audioFile?.name}
                    </p>
                  </div>

                  <div className="flex justify-center gap-4">
                    <button
                      onClick={togglePlayback}
                      className={`flex items-center gap-2 px-8 py-4 rounded-2xl font-semibold ${
                        darkMode 
                          ? 'bg-gray-700 text-white hover:bg-gray-600' 
                          : 'bg-gray-200 text-gray-800 hover:bg-gray-300'
                      }`}
                    >
                      {isPlaying ? <Pause size={20} /> : <Play size={20} />}
                      {isPlaying ? 'Pause' : 'Preview'}
                    </button>
                    <button
                      onClick={analyzeAudio}
                      className={`flex items-center gap-2 px-8 py-4 rounded-2xl font-semibold shadow-lg ${
                        darkMode
                          ? 'bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-500 hover:to-purple-500 text-white'
                          : 'bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 text-white'
                      }`}
                    >
                      <CheckCircle size={20} />
                      Analyze Speech
                    </button>
                  </div>
                  <audio ref={audioRef} src={audioURL} onEnded={() => setIsPlaying(false)} />

                  <div className="flex justify-center pt-4">
                    <button
                      onClick={reset}
                      className={`text-sm font-medium ${
                        darkMode 
                          ? 'text-gray-400 hover:text-gray-200' 
                          : 'text-gray-600 hover:text-gray-800'
                      }`}
                    >
                      â† Start Over
                    </button>
                  </div>
                </div>
              )}

              {step === 'uploading' && (
                <div className="text-center py-16">
                  <Loader className={`animate-spin mx-auto mb-6 ${
                    darkMode ? 'text-indigo-400' : 'text-indigo-600'
                  }`} size={56} />
                  <h3 className={`text-2xl font-bold mb-2 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
                    Analyzing Your Speech
                  </h3>
                  <p className={darkMode ? 'text-gray-400' : 'text-gray-600'}>
                    This usually takes 30-60 seconds...
                  </p>
                </div>
              )}

              {step === 'results' && results && (
                <div className="space-y-8">
                  <div className={`text-center p-8 rounded-3xl ${
                    darkMode 
                      ? 'bg-gradient-to-br from-indigo-600/20 to-purple-600/20 border border-indigo-500/30' 
                      : 'bg-gradient-to-br from-indigo-50 to-purple-50 border border-indigo-200'
                  }`}>
                    <p className={`text-sm font-semibold uppercase tracking-wider mb-2 ${
                      darkMode ? 'text-gray-400' : 'text-gray-600'
                    }`}>
                      Overall Score (Â±0.1)
                    </p>
                    <div className={`text-7xl font-black mb-1 ${
                      darkMode ? 'text-indigo-400' : 'text-indigo-600'
                    }`}>
                      {results.scores.total.toFixed(2)}
                    </div>
                    <p className={`text-xl font-semibold ${
                      darkMode ? 'text-gray-400' : 'text-gray-600'
                    }`}>
                      out of 2.0
                    </p>
                  </div>

                  <div className="grid grid-cols-3 gap-4">
                    {[
                      { label: 'Content', score: results.scores.content, max: 0.9 },
                      { label: 'Accuracy', score: results.scores.accuracy, max: 0.6 },
                      { label: 'Delivery', score: results.scores.delivery, max: 0.5 }
                    ].map((item, idx) => (
                      <div key={idx} className={`text-center p-6 rounded-2xl ${
                        darkMode ? 'bg-gray-900/50' : 'bg-gray-50'
                      }`}>
                        <div className={`text-xs font-bold uppercase tracking-wider mb-3 ${
                          darkMode ? 'text-gray-400' : 'text-gray-600'
                        }`}>
                          {item.label}
                        </div>
                        <div className={`text-3xl font-black ${getScoreColor(item.score, item.max)}`}>
                          {item.score.toFixed(2)}
                        </div>
                        <div className={`text-sm font-semibold mt-1 ${
                          darkMode ? 'text-gray-500' : 'text-gray-500'
                        }`}>
                          / {item.max.toFixed(1)}
                        </div>
                      </div>
                    ))}
                  </div>

                  <div className="space-y-4">
                    <h3 className={`text-xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}`}>
                      Detailed Feedback
                    </h3>

                    {[
                      { icon: 'ðŸ“', title: 'Content', feedback: results.feedback.content, color: 'blue' },
                      { icon: 'âœ“', title: 'Accuracy', feedback: results.feedback.accuracy, color: 'green' },
                      { icon: 'ðŸŽ¤', title: 'Delivery', feedback: results.feedback.delivery, color: 'purple' }
                    ].map((item, idx) => (
                      <div key={idx} className={`p-5 rounded-2xl border ${
                        darkMode 
                          ? `bg-${item.color}-950/20 border-${item.color}-800/30` 
                          : `bg-${item.color}-50 border-${item.color}-200`
                      }`}>
                        <h4 className={`font-bold mb-2 flex items-center gap-2 ${
                          darkMode ? `text-${item.color}-300` : `text-${item.color}-900`
                        }`}>
                          <span>{item.icon}</span>
                          {item.title}
                        </h4>
                        <p className={`text-sm leading-relaxed ${
                          darkMode ? 'text-gray-300' : 'text-gray-700'
                        }`}>
                          {item.feedback}
                        </p>
                      </div>
                    ))}
                  </div>

                  {results.sample_response && (
                    <div className={`p-6 rounded-2xl border ${
                      darkMode 
                        ? 'bg-amber-950/20 border-amber-800/30' 
                        : 'bg-gradient-to-br from-amber-50 to-orange-50 border-amber-200'
                    }`}>
                      <h4 className={`font-bold mb-4 flex items-center gap-2 text-lg ${
                        darkMode ? 'text-amber-300' : 'text-amber-900'
                      }`}>
                        <span>â­</span>
                        Sample 2.0 Response
                      </h4>
                      <div className={`text-sm leading-relaxed whitespace-pre-line ${
                        darkMode ? 'text-gray-300' : 'text-gray-800'
                      }`}>
                        {results.sample_response}
                      </div>
                    </div>
                  )}

                  <div className="flex gap-4 pt-4">
                    <button
                      onClick={downloadDocument}
                      className={`flex-1 flex items-center justify-center gap-2 px-8 py-4 rounded-2xl font-semibold shadow-lg ${
                        darkMode
                          ? 'bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-500 hover:to-purple-500 text-white'
                          : 'bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 text-white'
                      }`}
                    >
                      <Download size={20} />
                      Download Report
                    </button>
                    <button
                      onClick={reset}
                      className={`px-8 py-4 rounded-2xl font-semibold ${
                        darkMode 
                          ? 'bg-gray-700 text-gray-300 hover:bg-gray-600' 
                          : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                      }`}
                    >
                      New Analysis
                    </button>
                  </div>
                </div>
              )}
            </div>

            <div className={`text-center mt-8 text-sm ${
              darkMode ? 'text-gray-500' : 'text-gray-600'
            }`}>
              <p>Developed by Nguyen Hoang Minh Tri â€¢ HSGS Le Quy Don - Nam Nha Trang</p>
            </div>
          </>
        ) : (
          <SampleLibrary darkMode={darkMode} />
        )}
      </div>

      {showAdminPanel && isAuthenticated && (
        <AdminPanel darkMode={darkMode} onClose={() => setShowAdminPanel(false)} />
      )}
    </div>
  );
}

function AdminPanel({ darkMode, onClose }) {
  const [audioFile, setAudioFile] = useState(null);
  const [topic, setTopic] = useState('');
  const [speaker, setSpeaker] = useState('');
  const [score, setScore] = useState('2.0');
  const [transcript, setTranscript] = useState('');
  const [feedback, setFeedback] = useState('');
  const [uploading, setUploading] = useState(false);
  const [message, setMessage] = useState('');

  const handleUpload = async () => {
    if (!audioFile || !topic || !speaker || !transcript || !feedback) {
      setMessage('Please fill in all fields');
      return;
    }

    setUploading(true);
    setMessage('');

    const formData = new FormData();
    formData.append('audio', audioFile);
    formData.append('topic', topic);
    formData.append('speaker', speaker);
    formData.append('score', score);
    formData.append('transcript', transcript);
    formData.append('feedback', feedback);

    try {
      const response = await fetch(`${API_BASE_URL}/samples/upload`, {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (data.success) {
        setMessage('âœ“ Sample uploaded successfully!');
        setAudioFile(null);
        setTopic('');
        setSpeaker('');
        setScore('2.0');
        setTranscript('');
        setFeedback('');
      } else {
        setMessage('Error: ' + (data.error || 'Upload failed'));
      }
    } catch (err) {
      setMessage('Error: Connection failed');
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center z-50 p-4">
      <div className={`rounded-3xl max-w-2xl w-full max-h-[90vh] overflow-y-auto p-8 ${
        darkMode ? 'bg-gray-800' : 'bg-white'
      }`}>
        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center gap-3">
            <Lock className={darkMode ? 'text-indigo-400' : 'text-indigo-600'} size={24} />
            <h2 className={`text-3xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}`}>
              Admin Panel
            </h2>
          </div>
          <button
            onClick={onClose}
            className={`p-2 rounded-xl text-2xl font-bold ${
              darkMode 
                ? 'text-gray-400 hover:text-gray-200 hover:bg-gray-700' 
                : 'text-gray-400 hover:text-gray-600 hover:bg-gray-100'
            }`}
          >
            Ã—
          </button>
        </div>

        {message && (
          <div className={`mb-6 p-4 rounded-xl font-medium ${
            message.startsWith('âœ“') 
              ? darkMode 
                ? 'bg-green-500/10 text-green-300' 
                : 'bg-green-50 text-green-700'
              : darkMode 
                ? 'bg-red-500/10 text-red-300' 
                : 'bg-red-50 text-red-700'
          }`}>
            {message}
          </div>
        )}

        <div className="space-y-5">
          <div>
            <label className={`block text-sm font-semibold mb-2 ${
              darkMode ? 'text-gray-200' : 'text-gray-700'
            }`}>
              Audio File *
            </label>
            <input
              type="file"
              accept="audio/*"
              onChange={(e) => setAudioFile(e.target.files[0])}
              className={`w-full px-4 py-3 rounded-xl border-2 ${
                darkMode 
                  ? 'bg-gray-900/50 border-gray-700 text-white' 
                  : 'bg-gray-50 border-gray-200 text-gray-900'
              }`}
            />
          </div>

          <div>
            <label className={`block text-sm font-semibold mb-2 ${
              darkMode ? 'text-gray-200' : 'text-gray-700'
            }`}>
              Transcript *
            </label>
            <textarea
              value={transcript}
              onChange={(e) => setTranscript(e.target.value)}
              placeholder="Full transcript of the speech..."
              rows="6"
              className={`w-full px-4 py-3 rounded-xl border-2 resize-none ${
                darkMode 
                  ? 'bg-gray-900/50 border-gray-700 text-white placeholder-gray-500' 
                  : 'bg-gray-50 border-gray-200 text-gray-900 placeholder-gray-400'
              }`}
            />
          </div>

          <div>
            <label className={`block text-sm font-semibold mb-2 ${
              darkMode ? 'text-gray-200' : 'text-gray-700'
            }`}>
              Why This Sample Scored High *
            </label>
            <textarea
              value={feedback}
              onChange={(e) => setFeedback(e.target.value)}
              placeholder="Explain what makes this speech excellent..."
              rows="4"
              className={`w-full px-4 py-3 rounded-xl border-2 resize-none ${
                darkMode 
                  ? 'bg-gray-900/50 border-gray-700 text-white placeholder-gray-500' 
                  : 'bg-gray-50 border-gray-200 text-gray-900 placeholder-gray-400'
              }`}
            />
          </div>

          <button
            onClick={handleUpload}
            disabled={uploading}
            className={`w-full py-4 rounded-xl font-semibold shadow-lg ${
              uploading
                ? darkMode
                  ? 'bg-gray-700 text-gray-400 cursor-not-allowed'
                  : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                : darkMode
                  ? 'bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-500 hover:to-purple-500 text-white'
                  : 'bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 text-white'
            }`}
          >
            {uploading ? 'Uploading...' : 'Upload Sample'}
          </button>
        </div>
      </div>
    </div>
  );
}

function SampleLibrary({ darkMode }) {
  const [samples, setSamples] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSample, setSelectedSample] = useState(null);
  const [playingId, setPlayingId] = useState(null);
  const audioRef = useRef(null);

  React.useEffect(() => {
    fetchSamples();
  }, []);

  const fetchSamples = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/samples`);
      const data = await response.json();
      setSamples(data.samples || []);
    } catch (err) {
      console.error('Failed to fetch samples');
    } finally {
      setLoading(false);
    }
  };

  const filteredSamples = samples.filter(sample => 
    sample.topic.toLowerCase().includes(searchTerm.toLowerCase()) ||
    sample.speaker?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    sample.tags?.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const toggleAudio = (sampleId, audioUrl) => {
    if (playingId === sampleId) {
      audioRef.current?.pause();
      setPlayingId(null);
    } else {
      if (audioRef.current) {
        audioRef.current.src = audioUrl;
        audioRef.current.play();
        setPlayingId(sampleId);
      }
    }
  };

  const downloadAudio = (filename) => {
    const link = document.createElement('a');
    link.href = `http://127.0.0.1:5000/api/samples/download/${filename}`;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  if (loading) {
    return (
      <div className="text-center py-20">
        <Loader className={`animate-spin mx-auto mb-4 ${
          darkMode ? 'text-indigo-400' : 'text-indigo-600'
        }`} size={48} />
        <p className={`font-medium ${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>
          Loading samples...
        </p>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className={`text-4xl font-bold mb-3 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
          Sample Library
        </h2>
        <p className={`text-lg ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
          Learn from high-scoring speeches
        </p>
      </div>

      <div className={`rounded-2xl shadow-lg p-5 backdrop-blur-sm border ${
        darkMode 
          ? 'bg-gray-800/50 border-gray-700/50' 
          : 'bg-white/80 border-white/50'
      }`}>
        <input
          type="text"
          placeholder="Search by topic, speaker, or tag..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className={`w-full px-5 py-4 rounded-xl focus:ring-2 focus:ring-indigo-500 focus:outline-none font-medium ${
            darkMode 
              ? 'bg-gray-900/50 border-2 border-gray-700 text-white placeholder-gray-500' 
              : 'bg-gray-50 border-2 border-gray-200 text-gray-900 placeholder-gray-400'
          }`}
        />
      </div>

      {filteredSamples.length === 0 ? (
        <div className={`text-center py-16 rounded-2xl backdrop-blur-sm border ${
          darkMode 
            ? 'bg-gray-800/50 border-gray-700/50' 
            : 'bg-white/80 border-white/50'
        }`}>
          <FileAudio className={`mx-auto mb-4 ${
            darkMode ? 'text-gray-600' : 'text-gray-400'
          }`} size={56} />
          <p className={`font-semibold text-lg ${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>
            No samples found
          </p>
          <p className={`text-sm mt-2 ${darkMode ? 'text-gray-500' : 'text-gray-500'}`}>
            Try a different search term
          </p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-5">
          {filteredSamples.map((sample) => (
            <div
              key={sample.id}
              className={`rounded-2xl shadow-lg p-6 backdrop-blur-sm border ${
                darkMode 
                  ? 'bg-gray-800/50 border-gray-700/50' 
                  : 'bg-white/80 border-white/50'
              }`}
            >
              <div className="mb-4">
                <div className="flex items-start justify-between mb-3">
                  <h3 className={`font-bold text-lg flex-1 pr-2 ${
                    darkMode ? 'text-white' : 'text-gray-900'
                  }`}>
                    {sample.topic}
                  </h3>
                  <div className={`text-3xl font-black ${
                    darkMode ? 'text-indigo-400' : 'text-indigo-600'
                  }`}>
                    {sample.score}
                  </div>
                </div>

                <div className="flex flex-wrap gap-2 mb-3">
                  <span className={`px-3 py-1 text-xs font-semibold rounded-full ${
                    darkMode 
                      ? 'bg-indigo-500/20 text-indigo-300' 
                      : 'bg-indigo-100 text-indigo-700'
                  }`}>
                    {sample.topic}
                  </span>
                  {sample.speaker && (
                    <span className={`px-3 py-1 text-xs font-semibold rounded-full ${
                      darkMode 
                        ? 'bg-purple-500/20 text-purple-300' 
                        : 'bg-purple-100 text-purple-700'
                    }`}>
                      {sample.speaker}
                    </span>
                  )}
                  <span className={`px-3 py-1 text-xs font-semibold rounded-full ${
                    darkMode 
                      ? 'bg-green-500/20 text-green-300' 
                      : 'bg-green-100 text-green-700'
                  }`}>
                    {sample.score}/2.0
                  </span>
                </div>
              </div>

              <div className={`mb-4 p-3 rounded-xl text-sm ${
                darkMode ? 'bg-gray-900/50 text-gray-300' : 'bg-gray-50 text-gray-700'
              }`}>
                <p className="font-semibold mb-1">Transcript:</p>
                <p className="line-clamp-3">{sample.transcript}</p>
              </div>

              <div className={`mb-4 p-3 rounded-xl text-sm ${
                darkMode ? 'bg-green-500/10 text-green-300' : 'bg-green-50 text-green-700'
              }`}>
                <p className="font-semibold mb-1">Why it scored high:</p>
                <p className="line-clamp-2">{sample.feedback}</p>
              </div>

              <div className="flex gap-2">
                <button
                  onClick={() => toggleAudio(sample.id, sample.audioUrl)}
                  className={`flex-1 flex items-center justify-center gap-2 px-4 py-2.5 rounded-xl font-medium ${
                    playingId === sample.id
                      ? darkMode
                        ? 'bg-indigo-600 text-white'
                        : 'bg-indigo-600 text-white'
                      : darkMode
                        ? 'bg-gray-700 text-gray-300 hover:bg-gray-600'
                        : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                  }`}
                >
                  {playingId === sample.id ? <Pause size={16} /> : <Play size={16} />}
                  {playingId === sample.id ? 'Pause' : 'Play'}
                </button>
                <button
                  onClick={() => downloadAudio(sample.filename)}
                  className={`flex items-center justify-center gap-2 px-4 py-2.5 rounded-xl font-medium ${
                    darkMode 
                      ? 'bg-gray-700 text-gray-300 hover:bg-gray-600' 
                      : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                  }`}
                >
                  <Download size={16} />
                </button>
                <button
                  onClick={() => setSelectedSample(sample)}
                  className={`flex items-center justify-center px-4 py-2.5 rounded-xl font-medium ${
                    darkMode 
                      ? 'bg-indigo-600 text-white hover:bg-indigo-500' 
                      : 'bg-indigo-600 text-white hover:bg-indigo-700'
                  }`}
                >
                  View
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      <audio 
        ref={audioRef} 
        onEnded={() => setPlayingId(null)}
        onPause={() => setPlayingId(null)}
      />

      {selectedSample && (
        <div className="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center z-50 p-4">
          <div className={`rounded-3xl max-w-3xl w-full max-h-[85vh] overflow-y-auto p-8 ${
            darkMode ? 'bg-gray-800' : 'bg-white'
          }`}>
            <div className="flex justify-between items-start mb-6">
              <div>
                <h3 className={`text-3xl font-bold mb-3 ${
                  darkMode ? 'text-white' : 'text-gray-900'
                }`}>
                  {selectedSample.topic}
                </h3>
                <div className={`text-4xl font-black ${
                  darkMode ? 'text-indigo-400' : 'text-indigo-600'
                }`}>
                  {selectedSample.score} / 2.0
                </div>
              </div>
              <button
                onClick={() => setSelectedSample(null)}
                className={`p-2 rounded-xl text-2xl font-bold ${
                  darkMode 
                    ? 'text-gray-400 hover:text-gray-200 hover:bg-gray-700' 
                    : 'text-gray-400 hover:text-gray-600 hover:bg-gray-100'
                }`}
              >
                Ã—
              </button>
            </div>

            {selectedSample.transcript && (
              <div className="mb-6">
                <h4 className={`font-bold mb-3 text-lg ${
                  darkMode ? 'text-gray-200' : 'text-gray-900'
                }`}>
                  Transcript
                </h4>
                <p className={`text-sm leading-relaxed rounded-2xl p-5 ${
                  darkMode 
                    ? 'text-gray-300 bg-gray-900/50 border border-gray-700' 
                    : 'text-gray-700 bg-gray-50'
                }`}>
                  {selectedSample.transcript}
                </p>
              </div>
            )}

            {selectedSample.feedback && (
              <div>
                <h4 className={`font-bold mb-3 text-lg ${
                  darkMode ? 'text-gray-200' : 'text-gray-900'
                }`}>
                  Why This Speech Scored High
                </h4>
                <p className={`text-sm leading-relaxed rounded-2xl p-5 ${
                  darkMode 
                    ? 'text-gray-300 bg-green-950/20 border border-green-800/30' 
                    : 'text-gray-700 bg-green-50'
                }`}>
                  {selectedSample.feedback}
                </p>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
