import React, { useState, useRef, useEffect } from 'react';
import { Upload, Play, Pause, Download, CheckCircle, AlertCircle, Loader, FileAudio, Moon, Sun } from 'lucide-react';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://127.0.0.1:5000/api';

export default function SpeakUpApp() {
  const [currentPage, setCurrentPage] = useState('analyze');
  const [step, setStep] = useState('input');
  const [topic, setTopic] = useState('');
  const [audioFile, setAudioFile] = useState(null);
  const [audioURL, setAudioURL] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [results, setResults] = useState(null);
  const [error, setError] = useState(null);
  const [darkMode, setDarkMode] = useState(false);

  const audioRef = useRef(null);

  // Save dark mode preference
  useEffect(() => {
    const saved = localStorage.getItem('darkMode');
    if (saved) setDarkMode(JSON.parse(saved));
  }, []);

  useEffect(() => {
    localStorage.setItem('darkMode', JSON.stringify(darkMode));
  }, [darkMode]);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setAudioFile(file);
      setAudioURL(URL.createObjectURL(file));
      setStep('preview');
    }
  };

  const togglePlayback = () => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const analyzeAudio = async () => {
    if (!topic.trim()) {
      setError('Please enter a topic');
      return;
    }
    if (!audioFile) {
      setError('Please upload audio');
      return;
    }

    setStep('uploading');
    setError(null);

    const formData = new FormData();
    formData.append('audio', audioFile);
    formData.append('topic', topic);

    try {
      const response = await fetch(`${API_BASE_URL}/analyze`, {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (data.success) {
        setResults(data);
        setStep('results');
      } else {
        setError(data.error || 'Analysis failed');
        setStep('preview');
      }
    } catch (err) {
      setError('Connection failed. Make sure the backend is running.');
      setStep('preview');
    }
  };

  const downloadDocument = () => {
    if (results && results.document_url) {
      window.open(`http://127.0.0.1:5000${results.document_url}`, '_blank');
    }
  };

  const reset = () => {
    setStep('input');
    setTopic('');
    setAudioFile(null);
    setAudioURL(null);
    setResults(null);
    setError(null);
    setIsPlaying(false);
  };

  const getScoreColor = (score, max) => {
    const percentage = (score / max) * 100;
    if (percentage >= 80) return darkMode ? 'text-green-400' : 'text-green-600';
    if (percentage >= 60) return darkMode ? 'text-yellow-400' : 'text-yellow-600';
    return darkMode ? 'text-red-400' : 'text-red-600';
  };

  return (
    <div className={`min-h-screen ${
      darkMode 
        ? 'bg-gradient-to-br from-gray-900 via-slate-900 to-gray-900' 
        : 'bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50'
    }`}>
      {/* Glassmorphism Header */}
      <header className={`backdrop-blur-lg sticky top-0 z-50 border-b ${
        darkMode 
          ? 'bg-gray-900/70 border-gray-800' 
          : 'bg-white/70 border-gray-200'
      }`}>
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <div className={`w-10 h-10 rounded-xl flex items-center justify-center font-bold text-lg ${
                darkMode 
                  ? 'bg-gradient-to-br from-indigo-600 to-purple-600' 
                  : 'bg-gradient-to-br from-indigo-500 to-purple-500'
              } text-white shadow-lg`}>
                S
              </div>
              <h1 className={`text-2xl font-bold ${
                darkMode ? 'text-white' : 'text-gray-900'
              }`}>
                SpeakUp AI
              </h1>
            </div>

            <div className="flex items-center gap-4">
              <nav className="flex gap-2">
                <button
                  onClick={() => { setCurrentPage('analyze'); reset(); }}
                  className={`px-5 py-2.5 rounded-xl font-medium ${
                    currentPage === 'analyze' 
                      ? darkMode
                        ? 'bg-indigo-600 text-white shadow-lg shadow-indigo-500/50'
                        : 'bg-gradient-to-r from-indigo-600 to-purple-600 text-white shadow-lg shadow-indigo-300/50'
                      : darkMode 
                        ? 'text-gray-300 hover:bg-gray-800' 
                        : 'text-gray-600 hover:bg-gray-100'
                  }`}
                >
                  Analyze
                </button>
                <button
                  onClick={() => setCurrentPage('samples')}
                  className={`px-5 py-2.5 rounded-xl font-medium ${
                    currentPage === 'samples' 
                      ? darkMode
                        ? 'bg-indigo-600 text-white shadow-lg shadow-indigo-500/50'
                        : 'bg-gradient-to-r from-indigo-600 to-purple-600 text-white shadow-lg shadow-indigo-300/50'
                      : darkMode 
                        ? 'text-gray-300 hover:bg-gray-800' 
                        : 'text-gray-600 hover:bg-gray-100'
                  }`}
                >
                  Samples
                </button>
              </nav>

              <button
                onClick={() => setDarkMode(!darkMode)}
                className={`p-2.5 rounded-xl ${
                  darkMode 
                    ? 'bg-gray-800 text-yellow-400 hover:bg-gray-700' 
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                {darkMode ? <Sun size={20} /> : <Moon size={20} />}
              </button>

              <img 
                src="/logo.png" 
                alt="School Logo" 
                className={`h-11 w-11 rounded-xl object-cover border-2 ${
                  darkMode ? 'border-indigo-500/50' : 'border-indigo-300/50'
                }`}
                onError={(e) => {
                  e.target.src = 'data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><circle cx="50" cy="50" r="40" fill="%234F46E5"/><text x="50" y="60" text-anchor="middle" fill="white" font-size="35" font-family="Arial" font-weight="bold">HS</text></svg>';
                }}
              />
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-5xl mx-auto px-6 py-8">
        {currentPage === 'analyze' ? (
          <>
            <div className="text-center mb-10">
              <h2 className={`text-4xl font-bold mb-3 ${
                darkMode ? 'text-white' : 'text-gray-900'
              }`}>
                {step === 'results' ? 'Your Speech Analysis' : 'Analyze Your Speech'}
              </h2>
              <p className={`text-lg ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                Master NEC Speaking with AI-powered feedback
              </p>
            </div>

            {error && (
              <div className={`mb-6 p-4 rounded-2xl flex items-center gap-3 border backdrop-blur-sm ${
                darkMode 
                  ? 'bg-red-500/10 border-red-500/30 text-red-300' 
                  : 'bg-red-50 border-red-200 text-red-700'
              }`}>
                <AlertCircle size={20} />
                <span className="font-medium">{error}</span>
              </div>
            )}

            <div className={`rounded-3xl shadow-2xl p-8 backdrop-blur-sm border ${
              darkMode 
                ? 'bg-gray-800/50 border-gray-700/50' 
                : 'bg-white/80 border-white/50'
            }`}>
              {step === 'input' && (
                <div className="space-y-6">
                  <div>
                    <label className={`block text-sm font-semibold mb-3 ${
                      darkMode ? 'text-gray-200' : 'text-gray-700'
                    }`}>
