import React, { useState, useRef, useEffect } from 'react';
import { Upload, Mic, StopCircle, Play, Pause, Download, CheckCircle, AlertCircle, Loader, FileAudio } from 'lucide-react';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://127.0.0.1:5000/api';

export default function SpeakUpApp() {
  const [currentPage, setCurrentPage] = useState('analyze');
  const [step, setStep] = useState('input');
  const [topic, setTopic] = useState('');
  const [audioFile, setAudioFile] = useState(null);
  const [isRecording, setIsRecording] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [audioURL, setAudioURL] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [results, setResults] = useState(null);
  const [error, setError] = useState(null);

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const timerRef = useRef(null);
  const audioRef = useRef(null);

  useEffect(() => {
    if (isRecording) {
      timerRef.current = setInterval(() => {
        setRecordingTime(prev => {
          if (prev >= 300) {
            stopRecording();
            return 300;
          }
          return prev + 1;
        });
      }, 1000);
    } else {
      clearInterval(timerRef.current);
    }
    return () => clearInterval(timerRef.current);
  }, [isRecording]);

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorderRef.current = new MediaRecorder(stream);
      audioChunksRef.current = [];

      mediaRecorderRef.current.ondataavailable = (event) => {
        audioChunksRef.current.push(event.data);
      };

      mediaRecorderRef.current.onstop = () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/webm' });
        const url = URL.createObjectURL(audioBlob);
        setAudioURL(url);
        setAudioFile(new File([audioBlob], 'recording.webm', { type: 'audio/webm' }));
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorderRef.current.start();
      setIsRecording(true);
      setRecordingTime(0);
      setStep('recording');
    } catch (err) {
      setError('Microphone access denied. Please allow microphone access.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
    }
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setAudioFile(file);
      setAudioURL(URL.createObjectURL(file));
      setStep('recording');
    }
  };

  const togglePlayback = () => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const analyzeAudio = async () => {
    if (!topic.trim()) {
      setError('Please enter a topic');
      return;
    }
    if (!audioFile) {
      setError('Please record or upload audio');
      return;
    }

    setStep('uploading');
    setError(null);

    const formData = new FormData();
    formData.append('audio', audioFile);
    formData.append('topic', topic);

    try {
      const response = await fetch(`${API_BASE_URL}/analyze`, {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (data.success) {
        setResults(data);
        setStep('results');
      } else {
        setError(data.error || 'Analysis failed');
        setStep('recording');
      }
    } catch (err) {
      setError('Connection failed. Make sure the backend is running.');
      setStep('recording');
    }
  };

  const downloadDocument = () => {
    if (results && results.document_url) {
      window.open(`http://127.0.0.1:5000${results.document_url}`, '_blank');
    }
  };

  const reset = () => {
    setStep('input');
    setTopic('');
    setAudioFile(null);
    setAudioURL(null);
    setRecordingTime(0);
    setResults(null);
    setError(null);
    setIsPlaying(false);
  };

  const getScoreColor = (score, max) => {
    const percentage = (score / max) * 100;
    if (percentage >= 80) return 'text-green-600';
    if (percentage >= 60) return 'text-yellow-600';
    return 'text-red-600';
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 via-white to-purple-50">
      <header className="bg-white shadow-sm sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-6 py-4 flex items-center justify-between">
          <div className="flex items-center gap-4">
            <h1 className="text-3xl font-bold bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent">
              SpeakUp AI
            </h1>
          </div>

          <div className="flex items-center gap-6">
            <nav className="flex gap-4">
              <button
                onClick={() => { setCurrentPage('analyze'); reset(); }}
                className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                  currentPage === 'analyze' 
                    ? 'bg-indigo-600 text-white' 
                    : 'text-gray-600 hover:text-indigo-600'
                }`}
              >
                Analyze Speech
              </button>
              <button
                onClick={() => setCurrentPage('samples')}
                className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                  currentPage === 'samples' 
                    ? 'bg-indigo-600 text-white' 
                    : 'text-gray-600 hover:text-indigo-600'
                }`}
              >
                Sample Library
              </button>
            </nav>

            <img 
              src="/logo.png" 
              alt="School Logo" 
              className="h-12 w-12 rounded-full object-cover border-2 border-indigo-200"
              onError={(e) => {
                e.target.src = 'data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><circle cx="50" cy="50" r="40" fill="%234F46E5"/><text x="50" y="60" text-anchor="middle" fill="white" font-size="35" font-family="Arial" font-weight="bold">HS</text></svg>';
              }}
            />
          </div>
        </div>
      </header>

      <div className="max-w-4xl mx-auto p-6">
        {currentPage === 'analyze' ? (
          <>
            <div className="text-center mb-8 pt-4">
              <p className="text-gray-600">Master your speaking skills with AI-powered feedback</p>
            </div>

            {error && (
              <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-lg flex items-center gap-3">
                <AlertCircle className="text-red-600" size={20} />
                <span className="text-red-700">{error}</span>
              </div>
            )}

            <div className="bg-white rounded-2xl shadow-xl p-8">
              {step === 'input' && (
                <div className="space-y-6">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Topic / Question
                    </label>
                    <textarea
                      value={topic}
                      onChange={(e) => setTopic(e.target.value)}
                      placeholder="Enter the topic or question you'll be speaking about..."
                      className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent resize-none"
                      rows="4"
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <button
                      onClick={startRecording}
                      className="flex items-center justify-center gap-2 px-6 py-4 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition-colors"
                    >
                      <Mic size={20} />
                      Record Audio
                    </button>

                    <label className="flex items-center justify-center gap-2 px-6 py-4 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition-colors cursor-pointer">
                      <Upload size={20} />
                      Upload Audio
                      <input
                        type="file"
                        accept="audio/*"
                        onChange={handleFileUpload}
                        className="hidden"
                      />
                    </label>
                  </div>

                  <p className="text-sm text-gray-500 text-center">
                    Maximum recording time: 5 minutes
                  </p>
                </div>
              )}

              {step === 'recording' && (
                <div className="space-y-6">
                  <div className="text-center">
                    <div className="inline-flex items-center justify-center w-24 h-24 bg-indigo-100 rounded-full mb-4">
                      <FileAudio className="text-indigo-600" size={40} />
                    </div>
                    <h3 className="text-xl font-semibold mb-2">
                      {isRecording ? 'Recording...' : 'Audio Ready'}
                    </h3>
                    <p className="text-3xl font-mono text-indigo-600">
                      {formatTime(recordingTime)}
                    </p>
                  </div>

                  {isRecording && (
                    <div className="flex justify-center">
                      <button
                        onClick={stopRecording}
                        className="flex items-center gap-2 px-8 py-3 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
                      >
                        <StopCircle size={20} />
                        Stop Recording
                      </button>
                    </div>
                  )}

                  {!isRecording && audioURL && (
                    <>
                      <div className="flex justify-center gap-4">
                        <button
                          onClick={togglePlayback}
                          className="flex items-center gap-2 px-6 py-3 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors"
                        >
                          {isPlaying ? <Pause size={20} /> : <Play size={20} />}
                          {isPlaying ? 'Pause' : 'Play'}
                        </button>
                        <button
                          onClick={analyzeAudio}
                          className="flex items-center gap-2 px-6 py-3 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition-colors"
                        >
                          <CheckCircle size={20} />
                          Analyze
                        </button>
                      </div>
                      <audio ref={audioRef} src={audioURL} onEnded={() => setIsPlaying(false)} />
                    </>
                  )}

                  <div className="flex justify-center">
                    <button
                      onClick={reset}
                      className="text-gray-600 hover:text-gray-800 text-sm"
                    >
                      Start Over
                    </button>
                  </div>
                </div>
              )}

              {step === 'uploading' && (
                <div className="text-center py-12">
                  <Loader className="animate-spin text-indigo-600 mx-auto mb-4" size={48} />
                  <h3 className="text-xl font-semibold mb-2">Analyzing your speech...</h3>
                  <p className="text-gray-600">This may take a minute</p>
                </div>
              )}

              {step === 'results' && results && (
                <div className="space-y-6">
                  <div className="text-center pb-6 border-b">
                    <h3 className="text-2xl font-bold mb-2">Your Results</h3>
                    <div className="text-5xl font-bold text-indigo-600">
                      {results.scores.total.toFixed(2)}/2.0
                    </div>
                  </div>

                  <div className="grid grid-cols-3 gap-4">
                    <div className="text-center p-4 bg-gray-50 rounded-lg">
                      <div className="text-sm text-gray-600 mb-1">Content</div>
                      <div className={`text-2xl font-bold ${getScoreColor(results.scores.content, 0.9)}`}>
                        {results.scores.content.toFixed(2)}/0.9
                      </div>
                    </div>
                    <div className="text-center p-4 bg-gray-50 rounded-lg">
                      <div className="text-sm text-gray-600 mb-1">Accuracy</div>
                      <div className={`text-2xl font-bold ${getScoreColor(results.scores.accuracy, 0.6)}`}>
                        {results.scores.accuracy.toFixed(2)}/0.6
                      </div>
                    </div>
                    <div className="text-center p-4 bg-gray-50 rounded-lg">
                      <div className="text-sm text-gray-600 mb-1">Delivery</div>
                      <div className={`text-2xl font-bold ${getScoreColor(results.scores.delivery, 0.5)}`}>
                        {results.scores.delivery.toFixed(2)}/0.5
                      </div>
                    </div>
                  </div>

                  <div className="space-y-4">
                    <div className="p-4 bg-blue-50 rounded-lg">
                      <h4 className="font-semibold mb-2 text-blue-900"> Content</h4>
                      <p className="text-sm text-gray-700">{results.feedback.content}</p>
                    </div>
                    <div className="p-4 bg-green-50 rounded-lg">
                      <h4 className="font-semibold mb-2 text-green-900"> Accuracy</h4>
                      <p className="text-sm text-gray-700">{results.feedback.accuracy}</p>
                    </div>
                    <div className="p-4 bg-purple-50 rounded-lg">
                      <h4 className="font-semibold mb-2 text-purple-900"> Delivery</h4>
                      <p className="text-sm text-gray-700">{results.feedback.delivery}</p>
                    </div>
                  </div>

                  <div className="flex gap-4 pt-4">
                    <button
                      onClick={downloadDocument}
                      className="flex-1 flex items-center justify-center gap-2 px-6 py-3 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition-colors"
                    >
                      <Download size={20} />
                      Download Full Report
                    </button>
                    <button
                      onClick={reset}
                      className="px-6 py-3 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 transition-colors"
                    >
                      Try Again
                    </button>
                  </div>
                </div>
              )}
            </div>

            <div className="text-center mt-8 text-sm text-gray-500">
              <p>Powered by Groq AI  Made with  for language learners</p>
            </div>
          </>
        ) : (
          <SampleLibrary />
        )}
      </div>
    </div>
  );
}

function SampleLibrary() {
  const [samples, setSamples] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedSample, setSelectedSample] = useState(null);

  useEffect(() => {
    fetchSamples();
  }, []);

  const fetchSamples = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/samples`);
      const data = await response.json();
      setSamples(data.samples || []);
    } catch (err) {
      console.error('Failed to fetch samples');
    } finally {
      setLoading(false);
    }
  };

  const filteredSamples = samples.filter(sample => 
    sample.topic.toLowerCase().includes(searchTerm.toLowerCase()) ||
    sample.tags?.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  if (loading) {
    return (
      <div className="text-center py-12">
        <Loader className="animate-spin text-indigo-600 mx-auto mb-4" size={48} />
        <p className="text-gray-600">Loading samples...</p>
      </div>
    );
  }

  return (
    <div className="space-y-6 py-8">
      <div className="text-center mb-8">
        <h2 className="text-3xl font-bold mb-2">Sample Speech Library</h2>
        <p className="text-gray-600">Listen to high-scoring sample speeches</p>
      </div>

      <div className="bg-white rounded-lg shadow-md p-4">
        <input
          type="text"
          placeholder="Search by topic or tag..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
        />
      </div>

      {filteredSamples.length === 0 ? (
        <div className="text-center py-12 bg-white rounded-lg shadow-md">
          <FileAudio className="mx-auto mb-4 text-gray-400" size={48} />
          <p className="text-gray-600">No samples found</p>
          <p className="text-sm text-gray-500 mt-2">Try a different search term</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {filteredSamples.map((sample) => (
            <div
              key={sample.id}
              className="bg-white rounded-lg shadow-md p-6 hover:shadow-lg transition-shadow cursor-pointer"
              onClick={() => setSelectedSample(sample)}
            >
              <div className="flex items-start justify-between mb-3">
                <div className="flex-1">
                  <h3 className="font-semibold text-lg mb-1">{sample.topic}</h3>
                  <p className="text-sm text-gray-500">Duration: {Math.floor(sample.duration / 60)}:{(sample.duration % 60).toString().padStart(2, '0')}</p>
                </div>
                <div className="text-2xl font-bold text-indigo-600">
                  {sample.score}/2.0
                </div>
              </div>

              {sample.tags && (
                <div className="flex flex-wrap gap-2 mt-3">
                  {sample.tags.map((tag, idx) => (
                    <span key={idx} className="px-2 py-1 bg-indigo-100 text-indigo-700 text-xs rounded-full">
                      {tag}
                    </span>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      )}

      {selectedSample && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-2xl max-w-2xl w-full max-h-[90vh] overflow-y-auto p-8">
            <div className="flex justify-between items-start mb-6">
              <div>
                <h3 className="text-2xl font-bold mb-2">{selectedSample.topic}</h3>
                <div className="text-3xl font-bold text-indigo-600">{selectedSample.score}/2.0</div>
              </div>
              <button
                onClick={() => setSelectedSample(null)}
                className="text-gray-400 hover:text-gray-600 text-2xl"
              >

              </button>
            </div>

            {selectedSample.transcript && (
              <div className="mb-6">
                <h4 className="font-semibold mb-2">Transcript</h4>
                <p className="text-gray-700 text-sm leading-relaxed bg-gray-50 p-4 rounded-lg">
                  {selectedSample.transcript}
                </p>
              </div>
            )}

            {selectedSample.feedback && (
              <div className="space-y-3">
                <h4 className="font-semibold">Why This Speech Scored High</h4>
                <p className="text-sm text-gray-700 bg-green-50 p-4 rounded-lg">
                  {selectedSample.feedback}
                </p>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
