import React, { useState, useRef, useEffect } from "react";

const questions = [
  "Describe a memorable trip you’ve taken.",
  "Do you think technology makes people less creative?",
  "What are the benefits of studying abroad?",
  "What are the effects of social media on communication?",
  "Describe someone who inspires you and explain why.",
  "What are the advantages and disadvantages of working from home?",
];

const Simulation = () => {
  const [phase, setPhase] = useState("intro");
  const [selectedQuestion, setSelectedQuestion] = useState("");
  const [audioURL, setAudioURL] = useState(null);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [recordingChunks, setRecordingChunks] = useState([]);
  const [timeLeft, setTimeLeft] = useState(60);
  const [isRecording, setIsRecording] = useState(false);
  const [results, setResults] = useState(null);
  const [progress, setProgress] = useState(0);

  const timerRef = useRef(null);

  // helper function to start countdown
  const startCountdown = (duration, nextPhase) => {
    clearInterval(timerRef.current);
    setTimeLeft(duration);
    setProgress(0);
    timerRef.current = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timerRef.current);
          setProgress(100);
          if (nextPhase) setPhase(nextPhase);
          return 0;
        }
        setProgress(((duration - prev + 1) / duration) * 100);
        return prev - 1;
      });
    }, 1000);
  };

  // Randomize question
  const randomizeQuestion = () => {
    const random = questions[Math.floor(Math.random() * questions.length)];
    setSelectedQuestion(random);
    startCountdown(60, "inspection");
  };

  // Mic test
  const handleMicTest = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const testRecorder = new MediaRecorder(stream);
      testRecorder.start();
      setTimeout(() => {
        testRecorder.stop();
        stream.getTracks().forEach((t) => t.stop());
        alert("✅ Microphone works fine!");
      }, 1000);
    } catch (err) {
      alert("❌ Microphone not accessible!");
    }
  };

  // Recording logic
  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      const chunks = [];

      recorder.ondataavailable = (e) => chunks.push(e.data);
      recorder.onstop = () => {
        const blob = new Blob(chunks, { type: "audio/webm" });
        setAudioURL(URL.createObjectURL(blob));
        setRecordingChunks(chunks);
        stream.getTracks().forEach((t) => t.stop());
        setPhase("review");
      };

      recorder.start();
      setMediaRecorder(recorder);
      setIsRecording(true);
      startCountdown(300, "review");

      // auto stop after 5 minutes
      setTimeout(() => {
        if (recorder.state === "recording") recorder.stop();
      }, 300000);
    } catch (err) {
      alert("Microphone error: " + err.message);
    }
  };

  const stopRecording = () => {
    if (mediaRecorder && mediaRecorder.state === "recording") {
      mediaRecorder.stop();
      setIsRecording(false);
    }
  };

  // Analyze audio
  const handleAnalyze = async () => {
    if (!recordingChunks.length) return alert("No recording found!");
    const blob = new Blob(recordingChunks, { type: "audio/webm" });
    const formData = new FormData();
    formData.append("audio", blob, "simulation.webm");

    const res = await fetch("/api/analyze", {
      method: "POST",
      body: formData,
    });
    const data = await res.json();
    setResults(data);
  };

  // clear interval on unmount
  useEffect(() => {
    return () => clearInterval(timerRef.current);
  }, []);

  // Reusable progress bar
  const ProgressBar = ({ progress }) => (
    <div className="w-full bg-[#333] rounded-full h-2 mt-3">
      <div
        className="bg-[#1e90ff] h-2 rounded-full transition-all duration-500"
        style={{ width: `${progress}%` }}
      ></div>
    </div>
  );

  return (
    <div className="text-center text-white space-y-6">
      {/* ----------- Phase 1: INTRO ----------- */}
      {phase === "intro" && (
        <div className="space-y-4">
          <h1 className="text-2xl font-bold">NEC Speaking Simulation</h1>
          <p className="text-gray-400">
            Please prepare pen and paper for drafting ideas.
          </p>
          <button
            onClick={handleMicTest}
            className="px-4 py-2 bg-[#1e90ff] rounded hover:bg-[#1877d2]"
          >
            Test Microphone
          </button>
          <button
            onClick={randomizeQuestion}
            className="ml-2 px-4 py-2 bg-[#333] rounded hover:bg-[#444]"
          >
            Randomize Question
          </button>

          {selectedQuestion && (
            <div className="mt-6">
              <p className="italic text-lg text-[#d1d1d1] mb-2">
                {selectedQuestion}
              </p>
              <p className="text-gray-400">
                Reading time: {timeLeft}s remaining
              </p>
              <ProgressBar progress={progress} />
              <button
                onClick={() => setPhase("inspection")}
                className="mt-4 px-4 py-2 bg-[#333] rounded hover:bg-[#444]"
              >
                Finish reading question
              </button>
            </div>
          )}
        </div>
      )}

      {/* ----------- Phase 2: INSPECTION ----------- */}
      {phase === "inspection" && (
        <div>
          <h2 className="text-xl font-semibold mb-3">Preparation Time</h2>
          <p className="text-gray-400">Use this time to outline your ideas.</p>
          <p className="mt-4 text-lg">{timeLeft}s remaining</p>
          <ProgressBar progress={progress} />
          {timeLeft === 0 && setPhase("record")}
        </div>
      )}

      {/* ----------- Phase 3: RECORDING ----------- */}
      {phase === "record" && (
        <div>
          <h2 className="text-xl font-semibold mb-3">Recording in Progress</h2>
          <p className="text-gray-400">Speak clearly and confidently.</p>
          <p className="mt-4 text-lg">{timeLeft}s remaining</p>
          <ProgressBar progress={progress} />
          <button
            onClick={stopRecording}
            className="mt-6 px-4 py-2 bg-[#d9534f] rounded hover:bg-[#c9302c]"
          >
            Stop Recording
          </button>
        </div>
      )}

      {/* ----------- Phase 4: REVIEW ----------- */}
      {phase === "review" && (
        <div className="space-y-4">
          <h2 className="text-xl font-semibold">Check Your Recording</h2>
          {audioURL && <audio controls src={audioURL} className="mx-auto" />}
          <div className="flex justify-center gap-4 mt-4">
            <button
              onClick={handleAnalyze}
              className="px-4 py-2 bg-[#1e90ff] rounded hover:bg-[#1877d2]"
            >
              Analyze
            </button>
            <a
              href={audioURL}
              download="simulation_recording.webm"
              className="px-4 py-2 bg-[#333] rounded hover:bg-[#444]"
            >
              Download
            </a>
          </div>

          {results && (
            <div className="mt-6 text-left bg-[#111] p-4 rounded">
              <h3 className="text-lg font-semibold mb-2">Results:</h3>
              <pre className="text-gray-300 text-sm whitespace-pre-wrap">
                {JSON.stringify(results, null, 2)}
              </pre>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Simulation;
